    max-height:180px;
    overflow-y:auto;
  }
  .nido-lista h3{
    margin:0 0 10px 0;
    font-size:15px;
    color:#fef3c7;
  }
  .lista-canciones{
    list-style:none;
    padding:0;
    margin:0;
  }
  .lista-canciones li{
    padding:8px;
    margin:4px 0;
    background:rgba(245,245,220,0.15);
    border-radius:10px;
    cursor:pointer;
    font-size:12px;
    border:1px solid rgba(245,245,220,0.2);
    transition:all 0.2s;
  }
  .lista-canciones li:active{
    transform:scale(0.98);
  }
  .lista-canciones li.active{
    background:rgba(59,130,246,0.25);
    border-color:#3b82f6;
    box-shadow:0 2px 6px rgba(59,130,246,0.3);
  }

  /* BOT√ìN DE CARGA */
  .cargar-btn{
    width:100%;
    padding:12px;
    background:rgba(59,130,246,0.3);
    border-radius:12px;
    cursor:pointer;
    font-size:14px;
    border:2px solid rgba(59,130,246,0.5);
    margin:10px 0;
    transition:all 0.2s;
  }
  .cargar-btn:active{
    transform:scale(0.98);
    background:rgba(59,130,246,0.4);
  }

  /* ESTADO */
  .status{
    font-size:12px;
    color:#fef3c7;
    margin-top:12px;
    padding:8px;
    background:rgba(0,0,0,0.2);
    border-radius:8px;
  }
</style>
</head>
<body>
  <h1>Biotuner Nido Solar-Lunar v4.0 ‚òÄÔ∏èüåô</h1>

  <div class="panel">
    <!-- REPRODUCTOR PRINCIPAL CON CONTROLES AL LADO -->
    <div class="reproductor-principal">
      <!-- Bot√≥n anterior -->
      <div class="controles-al-lado">
        <div class="control-btn" id="prevBtn" title="Canci√≥n anterior">‚è™</div>
        <div class="control-btn" id="loadBtn" title="Cargar canciones">üìÅ</div>
      </div>

      <!-- NIDO SOLAR-LUNAR -->
      <div class="nido-solar-lunar">
        <div class="mitad-sol" id="playPauseBtn">
          <div class="control-text">SOL<br>‚ñ∂Ô∏è</div>
        </div>
        <div class="mitad-luna" id="stopBtn">
          <div class="control-text">LUNA<br>‚èπÔ∏è</div>
        </div>
      </div>

      <!-- Bot√≥n siguiente -->
      <div class="controles-al-lado">
        <div class="control-btn" id="nextBtn" title="Siguiente canci√≥n">‚è©</div>
        <div style="width:45px; height:45px;"></div> <!-- Espacio para alinear -->
      </div>
    </div>

    <input type="file" id="fileInput" accept="audio/*" multiple style="display:none;">

    <!-- HUEVITOS DE FRECUENCIAS -->
    <div class="huevitos-container">
      <div class="huevito" data-freq="432" style="background:linear-gradient(135deg,#93c5fd,#3b82f6);">
        <div class="huevito-emoji">üåç</div>
        <div class="huevito-freq">432Hz</div>
        <div class="huevito-state">Conexi√≥n Tierra</div>
      </div>
      <div class="huevito" data-freq="436" style="background:linear-gradient(135deg,#86efac,#16a34a);">
        <div class="huevito-emoji">üîÑ</div>
        <div class="huevito-freq">436Hz</div>
        <div class="huevito-state">Transmutaci√≥n</div>
      </div>
      <div class="huevito" data-freq="440" style="background:linear-gradient(135deg,#fde047,#eab308);">
        <div class="huevito-emoji">‚öñÔ∏è</div>
        <div class="huevito-freq">440Hz</div>
        <div class="huevito-state">Equilibrio</div>
      </div>
      <div class="huevito" data-freq="444" style="background:linear-gradient(135deg,#f97316,#dc2626);">
        <div class="huevito-emoji">üß¨</div>
        <div class="huevito-freq">444Hz</div>
        <div class="huevito-state">Reparaci√≥n ADN</div>
      </div>
      <div class="huevito" data-freq="448" style="background:linear-gradient(135deg,#c084fc,#7c3aed);">
        <div class="huevito-emoji">üò¥</div>
        <div class="huevito-freq">448Hz</div>
        <div class="huevito-state">Sue√±o Profundo</div>
      </div>
      <div class="huevito" data-freq="452" style="background:linear-gradient(135deg,#fda4af,#e11d48);">
        <div class="huevito-emoji">üíñ</div>
        <div class="huevito-freq">452Hz</div>
        <div class="huevito-state">Amor Incondicional</div>
      </div>
      <div class="huevito" data-freq="456" style="background:linear-gradient(135deg,#67e8f9,#0e7490);">
        <div class="huevito-emoji">üåà</div>
        <div class="huevito-freq">456Hz</div>
        <div class="huevito-state">Armon√≠a Total</div>
      </div>
    </div>

    <!-- LISTA COMO NIDO - AHORA M√ÅS VISIBLE -->
    <div class="nido-lista">
      <h3>üê£ Nido de Canciones</h3>
      <ul class="lista-canciones" id="playlist">
        <li style="opacity:0.7;text-align:center;">Toca üìÅ para agregar canciones al nido</li>
      </ul>
    </div>

    <div id="status" class="status">Listo para cargar canciones</div>
  </div>

<script>
/* Variables y elementos */
let audioCtx = null;
let masterGain = null;
let sourceNode = null;
let currentBuffer = null;
let playlistData = [];
let currentIndex = -1;
let isPlaying = false;
let isPaused = false;
let startTime = 0;
let pausedAt = 0;
let volume = 0.7;
let currentFrequency = 432;

const fileInput = document.getElementById('fileInput');
const playPauseBtn = document.getElementById('playPauseBtn');
const stopBtn = document.getElementById('stopBtn');
const prevBtn = document.getElementById('prevBtn');
const nextBtn = document.getElementById('nextBtn');
const loadBtn = document.getElementById('loadBtn');
const status = document.getElementById('status');
const playlistEl = document.getElementById('playlist');
const huevitos = document.querySelectorAll('.huevito');

/* Inicializar audio */
function initAudioIfNeeded() {
  if (!audioCtx) {
    audioCtx = new (window.AudioContext || window.webkitAudioContext)();
    masterGain = audioCtx.createGain();
    masterGain.gain.value = volume;
    masterGain.connect(audioCtx.destination);
  }
}

/* Cargar archivos */
loadBtn.addEventListener('click', () => fileInput.click());
fileInput.addEventListener('change', async (e) => {
  const files = Array.from(e.target.files || []);

  if (playlistData.length === 0) {
    playlistEl.innerHTML = '';
  }

  if (files.length === 0) {
    if (playlistData.length === 0) {
      playlistEl.innerHTML = '<li style="opacity:0.7;text-align:center;">Toca üìÅ para agregar canciones al nido</li>';
    }
    status.textContent = "No se seleccionaron archivos nuevos.";
    return;
  }

  initAudioIfNeeded();

  if (currentIndex === -1 && files.length > 0) {
    currentIndex = 0;
  }

  for (const file of files) {
    const li = document.createElement('li');
    li.textContent = file.name + " (cargando...)";
    playlistEl.appendChild(li);

    const newIndex = playlistData.length;
    playlistData.push({file, li, buffer: null});

    try {
      const arrayBuffer = await file.arrayBuffer();
      const buffer = await audioCtx.decodeAudioData(arrayBuffer);

      playlistData[newIndex].buffer = buffer;
      const duration = Math.round(buffer.duration);
      li.textContent = file.name + " (" + Math.floor(duration/60) + ":" + String(duration%60).padStart(2,'0') + ")";

      li.onclick = () => { 
        currentIndex = newIndex; 
        stopAudio();
        playAudio(); 
      };

    } catch(err) {
      li.textContent = file.name + " (error al cargar)";
      console.error("Error decoding audio:", err);
    }
  }

  status.textContent = `Se agregaron ${files.length} canciones al nido. Total: ${playlistData.length}`;
  fileInput.value = '';
});

/* Sistema de audio */
function startSourceAt(offsetSec) {
  if (!audioCtx || currentIndex < 0) return;

  sourceNode = audioCtx.createBufferSource();
  sourceNode.buffer = playlistData[currentIndex].buffer;
  sourceNode.playbackRate.value = currentFrequency / 440;
  sourceNode.connect(masterGain);

  sourceNode.onended = () => {
    if (isPlaying) {
      isPlaying = false;
      isPaused = false;
      pausedAt = 0;
      playPauseBtn.querySelector('.control-text').innerHTML = "SOL<br>‚ñ∂Ô∏è";
      status.textContent = "Canci√≥n terminada";
    }
  };

  sourceNode.start(0, offsetSec);
  startTime = audioCtx.currentTime - offsetSec;
  isPlaying = true;
  isPaused = false;
  playPauseBtn.querySelector('.control-text').innerHTML = "SOL<br>‚è∏Ô∏è";
}

function playAudio() {
  if (!audioCtx) initAudioIfNeeded();
  if (playlistData.length === 0) return;
  if (currentIndex < 0) currentIndex = 0;

  if (isPlaying) return;

  const buffer = playlistData[currentIndex].buffer;
  if (!buffer) return;
  currentBuffer = buffer;
  startSourceAt(pausedAt);

  if (masterGain) masterGain.gain.value = volume;

  playlistEl.querySelectorAll('li').forEach((li, i) => {
    li.classList.toggle('active', i === currentIndex);
  });

  status.textContent = `Reproduciendo: ${playlistData[currentIndex].file.name} | ${currentFrequency}Hz`;
}

function pauseAudio() {
  if (!audioCtx || !isPlaying || !sourceNode) return;

  pausedAt = Math.max(0, audioCtx.currentTime - startTime);
  try { sourceNode.stop(); } catch(e) {}
  isPlaying = false;
  isPaused = true;
  playPauseBtn.querySelector('.control-text').innerHTML = "SOL<br>‚ñ∂Ô∏è";
  status.textContent = "Pausado";
}

function stopAudio() {
  if (sourceNode) { 
    try { sourceNode.stop(); } catch(e) {} 
  }
  isPlaying = false;
  isPaused = false;
  pausedAt = 0;
  playPauseBtn.querySelector('.control-text').innerHTML = "SOL<br>‚ñ∂Ô∏è";
  status.textContent = "Detenido";

  playlistEl.querySelectorAll('li').forEach(li => {
    li.classList.remove('active');
  });
}

/* Controles */
playPauseBtn.addEventListener('click', () => {
  if (!audioCtx) initAudioIfNeeded();
  if (!isPlaying && !isPaused) {
    playAudio();
  } else if (isPlaying) {
    pauseAudio();
  } else if (isPaused) {
    playAudio();
  }
});

stopBtn.addEventListener('click', stopAudio);

prevBtn.addEventListener('click', () => {
  if (playlistData.length === 0) return;
  currentIndex = (currentIndex - 1 + playlistData.length) % playlistData.length;
  pausedAt = 0;
  stopAudio();
  playAudio();
});

nextBtn.addEventListener('click', () => {
  if (playlistData.length === 0) return;
  currentIndex = (currentIndex + 1) % playlistData.length;
  pausedAt = 0;
  stopAudio();
  playAudio();
});

/* Sistema de huevitos */
huevitos.forEach(huevito => {
  huevito.addEventListener('click', () => {
    huevitos.forEach(h => h.classList.remove('active'));
    huevito.classList.add('active');

    currentFrequency = parseInt(huevito.dataset.freq);

    if (isPlaying && sourceNode) {
      const now = audioCtx.currentTime;
      sourceNode.playbackRate.cancelScheduledValues(now);
      sourceNode.playbackRate.linearRampToValueAtTime(currentFrequency / 440, now + 0.2);
      status.textContent = `Frecuencia cambiada: ${currentFrequency}Hz`;
    } else {
      status.textContent = `Frecuencia preparada: ${currentFrequency}Hz`;
    }
  });
});

// Activar huevito por defecto (432Hz)
document.querySelector('.huevito[data-freq="432"]').classList.add('active');

</script>
</body>
