fileInput.addEventListener('change', async (e) => {
  const files = Array.from(e.target.files || []);

  if (playlistData.length === 0) {
    playlistEl.innerHTML = '';
  }

  if (files.length === 0) {
    if (playlistData.length === 0) {
      playlistEl.innerHTML = '<li style="opacity:0.7;text-align:center;">Toca 📁 para agregar canciones al nido</li>';
    }
    status.textContent = "No se seleccionaron archivos nuevos.";
    return;
  }

  initAudioIfNeeded();

  if (currentIndex === -1 && files.length > 0) {
    currentIndex = 0;
  }

  for (const file of files) {
    const li = document.createElement('li');
    li.textContent = file.name + " (cargando...)";
    playlistEl.appendChild(li);

    const newIndex = playlistData.length;
    playlistData.push({file, li, buffer: null});

    try {
      const arrayBuffer = await file.arrayBuffer();
      const buffer = await audioCtx.decodeAudioData(arrayBuffer);

      playlistData[newIndex].buffer = buffer;
      const duration = Math.round(buffer.duration);
      li.textContent = file.name + " (" + Math.floor(duration/60) + ":" + String(duration%60).padStart(2,'0') + ")";

      li.onclick = () => { 
        currentIndex = newIndex; 
        stopAudio();
        playAudio(); 
      };

    } catch(err) {
      li.textContent = file.name + " (error al cargar)";
      console.error("Error decoding audio:", err);
    }
  }

  status.textContent = `Se agregaron ${files.length} canciones al nido. Total: ${playlistData.length}`;
  fileInput.value = '';
});

/* Sistema de audio */
function startSourceAt(offsetSec) {
  if (!audioCtx || currentIndex < 0) return;

  sourceNode = audioCtx.createBufferSource();
  sourceNode.buffer = playlistData[currentIndex].buffer;
  sourceNode.playbackRate.value = currentFrequency / 440;
  sourceNode.connect(masterGain);

  sourceNode.onended = () => {
    if (isPlaying) {
      isPlaying = false;
      isPaused = false;
      pausedAt = 0;
      playPauseBtn.querySelector('.control-text').innerHTML = "SOL<br>▶️";
      status.textContent = "Canción terminada";
    }
  };

  sourceNode.start(0, offsetSec);
  startTime = audioCtx.currentTime - offsetSec;
  isPlaying = true;
  isPaused = false;
  playPauseBtn.querySelector('.control-text').innerHTML = "SOL<br>⏸️";
}

function playAudio() {
  if (!audioCtx) initAudioIfNeeded();
  if (playlistData.length === 0) return;
  if (currentIndex < 0) currentIndex = 0;

  if (isPlaying) return;

  const buffer = playlistData[currentIndex].buffer;
  if (!buffer) return;
  currentBuffer = buffer;
  startSourceAt(pausedAt);

  if (masterGain) masterGain.gain.value = volume;

  playlistEl.querySelectorAll('li').forEach((li, i) => {
    li.classList.toggle('active', i === currentIndex);
  });

  status.textContent = `Reproduciendo: ${playlistData[currentIndex].file.name} | ${currentFrequency}Hz`;
}

function pauseAudio() {
  if (!audioCtx || !isPlaying || !sourceNode) return;

  pausedAt = Math.max(0, audioCtx.currentTime - startTime);
  try { sourceNode.stop(); } catch(e) {}
  isPlaying = false;
  isPaused = true;
  playPauseBtn.querySelector('.control-text').innerHTML = "SOL<br>▶️";
  status.textContent = "Pausado";
}

function stopAudio() {
  if (sourceNode) { 
    try { sourceNode.stop(); } catch(e) {} 
  }
  isPlaying = false;
  isPaused = false;
  pausedAt = 0;
  playPauseBtn.querySelector('.control-text').innerHTML = "SOL<br>▶️";
  status.textContent = "Detenido";

  playlistEl.querySelectorAll('li').forEach(li => {
    li.classList.remove('active');
  });
}

/* Controles */
playPauseBtn.addEventListener('click', () => {
  if (!audioCtx) initAudioIfNeeded();
  if (!isPlaying && !isPaused) {
    playAudio();
  } else if (isPlaying) {
    pauseAudio();
  } else if (isPaused) {
    playAudio();
  }
});

stopBtn.addEventListener('click', stopAudio);

prevBtn.addEventListener('click', () => {
  if (playlistData.length === 0) return;
  currentIndex = (currentIndex - 1 + playlistData.length) % playlistData.length;
  pausedAt = 0;
  stopAudio();
  playAudio();
});

nextBtn.addEventListener('click', () => {
  if (playlistData.length === 0) return;
  currentIndex = (currentIndex + 1) % playlistData.length;
  pausedAt = 0;
  stopAudio();
  playAudio();
});

/* Sistema de huevitos */
huevitos.forEach(huevito => {
  huevito.addEventListener('click', () => {
    huevitos.forEach(h => h.classList.remove('active'));
    huevito.classList.add('active');

    currentFrequency = parseInt(huevito.dataset.freq);

    if (isPlaying && sourceNode) {
      const now = audioCtx.currentTime;
      sourceNode.playbackRate.cancelScheduledValues(now);
      sourceNode.playbackRate.linearRampToValueAtTime(currentFrequency / 440, now + 0.2);
      status.textContent = `Frecuencia cambiada: ${currentFrequency}Hz`;
    } else {
      status.textContent = `Frecuencia preparada: ${currentFrequency}Hz`;
    }
  });
});

// Activar huevito por defecto (432Hz)
document.querySelector('.huevito[data-freq="432"]').classList.add('active');

</script>
