    <script>
        class Biotuner {
            constructor() {
                this.audioContext = null;
                this.oscillator = null;
                this.frecuenciaActiva = null;
                this.modoActivo = null; // 'sol' o 'luna'
                this.inicializarAudio();
                this.inicializarControles();
            }

            inicializarAudio() {
                try {
                    this.audioContext = new (window.AudioContext || window.webkitAudioContext)();
                } catch (e) {
                    console.log('Audio no soportado:', e);
                }
            }

            inicializarControles() {
                // Botones SOL y LUNA
                document.getElementById('btnSol').addEventListener('click', () => {
                    this.activarModo('sol');
                });

                document.getElementById('btnLuna').addEventListener('click', () => {
                    this.activarModo('luna');
                });

                // Bot√≥n STOP
                document.getElementById('btnStop').addEventListener('click', () => {
                    this.detenerAudio();
                });

                // Botones de frecuencia
                document.querySelectorAll('.btn-frecuencia').forEach(boton => {
                    boton.addEventListener('click', () => {
                        const frecuencia = parseInt(boton.dataset.freq);
                        this.activarFrecuencia(frecuencia, boton);
                    });
                });

                // Bot√≥n de canciones
                document.getElementById('btnCanciones').addEventListener('click', () => {
                    this.agregarCanciones();
                });
            }

            activarModo(modo) {
                this.modoActivo = modo;

                // Actualizar interfaz
                document.getElementById('btnSol').classList.toggle('activa', modo === 'sol');
                document.getElementById('btnLuna').classList.toggle('activa', modo === 'luna');

                console.log(`Modo ${modo.toUpperCase()} activado`);
            }

            activarFrecuencia(frecuencia, boton) {
                if (!this.modoActivo) {
                    alert('Primero selecciona SOL o LUNA');
                    return;
                }

                // Detener audio anterior
                this.detenerAudio();

                // Actualizar interfaz
                document.querySelectorAll('.btn-frecuencia').forEach(btn => {
                    btn.classList.remove('activa');
                });
                boton.classList.add('activa');

                // Reproducir nueva frecuencia
                this.reproducirFrecuencia(frecuencia);

                this.frecuenciaActiva = frecuencia;
                console.log(`Frecuencia ${frecuencia}Hz activada en modo ${this.modoActivo}`);
            }

            reproducirFrecuencia(frecuencia) {
                if (!this.audioContext) return;

                this.oscillator = this.audioContext.createOscillator();
                const gainNode = this.audioContext.createGain();

                this.oscillator.connect(gainNode);
                gainNode.connect(this.audioContext.destination);

                this.oscillator.frequency.setValueAtTime(frecuencia, this.audioContext.currentTime);
                this.oscillator.type = 'sine';

                // Volumen bajo para no da√±ar o√≠dos
                gainNode.gain.setValueAtTime(0.1, this.audioContext.currentTime);

                this.oscillator.start();
            }

            detenerAudio() {
                if (this.oscillator) {
                    this.oscillator.stop();
                    this.oscillator = null;
                }

                // Limpiar interfaz
                document.querySelectorAll('.btn-frecuencia').forEach(btn => {
                    btn.classList.remove('activa');
                });
                document.getElementById('btnSol').classList.remove('activa');
                document.getElementById('btnLuna').classList.remove('activa');

                this.frecuenciaActiva = null;
                this.modoActivo = null;
                console.log('Audio detenido');
            }

            agregarCanciones() {
                // En una versi√≥n futura, aqu√≠ ir√≠a la l√≥gica para subir canciones
                alert('Funcionalidad de agregar canciones - Pr√≥ximamente!');
                console.log('Agregar canciones al nido');
            }
        }

        // Inicializar el Biotuner cuando se carga la p√°gina
        document.addEventListener('DOMContentLoaded', () => {
            new Biotuner();
            console.log('Biotuner Nido Solar-Lunar v4.0 iniciado');
        });
    </script>
</body>
