    constructor() {
        this.audioCtx = null;
        this.masterGain = null;
        this.sourceNode = null;
        this.currentBuffer = null;
        this.volume = 0.7;
        this.currentFrequency = 440;
        this.isPlaying = false;
        this.isPaused = false;
        this.startTime = 0;
        this.pausedAt = 0;
    }

    init() {
        if (!this.audioCtx) {
            this.audioCtx = new (window.AudioContext || window.webkitAudioContext)();
            this.masterGain = this.audioCtx.createGain();
            this.masterGain.gain.value = this.volume;
            this.masterGain.connect(this.audioCtx.destination);
        }
    }

    startSourceAt(offsetSec, buffer, frequency) {
        if (!this.audioCtx) return null;

        this.sourceNode = this.audioCtx.createBufferSource();
        this.sourceNode.buffer = buffer;
        this.sourceNode.playbackRate.value = frequency / 440;
        this.sourceNode.connect(this.masterGain);

        this.sourceNode.start(0, offsetSec);
        this.startTime = this.audioCtx.currentTime - offsetSec;
        this.isPlaying = true;
        this.isPaused = false;

        return this.sourceNode;
    }

    stopSource() {
        if (this.sourceNode) {
            try { this.sourceNode.stop(); } catch(e) {}
            this.sourceNode = null;
        }
    }

    changeFrequency(newFrequency) {
        this.currentFrequency = newFrequency;
        if (this.isPlaying && this.sourceNode) {
            const now = this.audioCtx.currentTime;
            this.sourceNode.playbackRate.cancelScheduledValues(now);
            this.sourceNode.playbackRate.linearRampToValueAtTime(newFrequency / 440, now + 0.2);
        }
    }

    setVolume(newVolume) {
        this.volume = newVolume;
        if (this.masterGain) {
            this.masterGain.gain.value = newVolume;
        }
    }
}2. üìÅ playlist-manager.jsjavascriptCopyDownloadclass PlaylistManager {
    constructor() {
        this.playlistData = [];
        this.currentIndex = -1;
        this.onPlaylistUpdate = null;
        this.onTrackChange = null;
    }

    async addFiles(files) {
        const newTracks = [];

        for (const file of files) {
            const track = {
                file: file,
                name: file.name,
                buffer: null,
                duration: 0,
                status: 'loading'
            };

            this.playlistData.push(track);
            newTracks.push(track);

            try {
                const arrayBuffer = await file.arrayBuffer();
                track.buffer = await this.decodeAudioData(arrayBuffer);
                track.duration = Math.round(track.buffer.duration);
                track.status = 'loaded';
            } catch(err) {
                track.status = 'error';
                console.error("Error decoding audio:", err);
            }
        }

        if (this.onPlaylistUpdate) {
            this.onPlaylistUpdate(this.playlistData);
        }

        return newTracks;
    }

    decodeAudioData(arrayBuffer) {
        return new Promise((resolve, reject) => {
            if (!window.AudioContext) {
                reject(new Error("Web Audio API no soportada"));
                return;
            }
            const audioCtx = new (window.AudioContext || window.webkitAudioContext)();
            audioCtx.decodeAudioData(arrayBuffer, resolve, reject);
        });
    }

    setCurrentIndex(index) {
        if (index >= 0 && index < this.playlistData.length) {
            this.currentIndex = index;
            if (this.onTrackChange) {
                this.onTrackChange(this.getCurrentTrack());
            }
        }
    }

    getCurrentTrack() {
        return this.currentIndex >= 0 ? this.playlistData[this.currentIndex] : null;
    }

    next() {
        if (this.playlistData.length === 0) return -1;
        const newIndex = (this.currentIndex + 1) % this.playlistData.length;
        this.setCurrentIndex(newIndex);
        return newIndex;
    }

    previous() {
        if (this.playlistData.length === 0) return -1;
        const newIndex = (this.currentIndex - 1 + this.playlistData.length) % this.playlistData.length;
        this.setCurrentIndex(newIndex);
        return newIndex;
    }

    clear() {
        this.playlistData = [];
        this.currentIndex = -1;
        if (this.onPlaylistUpdate) {
            this.onPlaylistUpdate(this.playlistData);
        }
    }
