            if (this.onNext) this.onNext();
        });

        this.elements.loadBtn.addEventListener('click', () => {
            this.elements.fileInput.click();
        });

        this.elements.fileInput.addEventListener('change', (e) => {
            if (this.onLoadFiles) this.onLoadFiles(Array.from(e.target.files));
        });
    }

    setupHuevitos() {
        this.elements.huevitos.forEach(huevito => {
            huevito.addEventListener('click', () => {
                const frequency = parseInt(huevito.dataset.freq);

                // Actualizar estado visual
                this.elements.huevitos.forEach(h => h.classList.remove('active'));
                huevito.classList.add('active');

                if (this.onFrequencyChange) {
                    this.onFrequencyChange(frequency);
                }

                this.showPajarito(1200);
            });
        });
    }

    updatePlaylist(playlistData, currentIndex) {
        this.elements.playlist.innerHTML = '';

        if (playlistData.length === 0) {
            const emptyItem = document.createElement('li');
            emptyItem.style.opacity = '0.7';
            emptyItem.style.textAlign = 'center';
            emptyItem.textContent = 'Toca üìÅ para agregar canciones al nido';
            this.elements.playlist.appendChild(emptyItem);
            return;
        }

        playlistData.forEach((track, index) => {
            const li = document.createElement('li');

            let durationText = '';
