                const minutes = Math.floor(track.duration / 60);
                const seconds = String(track.duration % 60).padStart(2, '0');
                durationText = ` (${minutes}:${seconds})`;
            }

            li.textContent = track.name + durationText;
            li.classList.toggle('active', index === currentIndex);

            li.addEventListener('click', () => {
                if (this.onTrackSelect) this.onTrackSelect(index);
            });

            this.elements.playlist.appendChild(li);
        });
    }

    updateStatus(message) {
        if (this.elements.status) {
            this.elements.status.textContent = message;
        }
    }

    updatePlayButton(isPlaying) {
        const textElement = this.elements.playPauseBtn.querySelector('.control-text');
        if (textElement) {
            textElement.innerHTML = isPlaying ? "SOL<br>‚è∏Ô∏è PAUSA" : "SOL<br>‚ñ∂Ô∏è PLAY/PAUSE";
        }
    }

    showPajarito(duration = 2500) {
        if (this.elements.pajaritoOverlay) {
            this.elements.pajaritoOverlay.style.display = 'flex';
            setTimeout(() => {
                this.elements.pajaritoOverlay.style.display = 'none';
            }, duration);
        }
    }
