            fileInput: document.getElementById('fileInput'),
            status: document.getElementById('status'),
            playlist: document.getElementById('playlist'),
            huevitos: document.querySelectorAll('.huevito'),
            pajaritoOverlay: document.getElementById('pajaritoOverlay')
        };

        this.setupEventListeners();
        this.setupHuevitos();
    }

    setupEventListeners() {
        this.elements.playPauseBtn.addEventListener('click', () => {
            if (this.onPlayPause) this.onPlayPause();
        });

        this.elements.stopBtn.addEventListener('click', () => {
            if (this.onStop) this.onStop();
        });

        this.elements.prevBtn.addEventListener('click', () => {
            if (this.onPrevious) this.onPrevious();
        });

        this.elements.nextBtn.addEventListener('click', () => {
            if (this.onNext) this.onNext();
        });

        this.elements.loadBtn.addEventListener('click', () => {
            this.elements.fileInput.click();
        });

        this.elements.fileInput.addEventListener('change', (e) => {
            if (this.onLoadFiles) this.onLoadFiles(Array.from(e.target.files));
        });
    }

    setupHuevitos() {
        this.elements.huevitos.forEach(huevito => {
            huevito.addEventListener('click', () => {
                const frequency = parseInt(huevito.dataset.freq);

                // Actualizar estado visual
                this.elements.huevitos.forEach(h => h.classList.remove('active'));
                huevito.classList.add('active');

                if (this.onFrequencyChange) {
                    this.onFrequencyChange(frequency);
                }

                this.showPajarito(1200);
            });
        });
    }

    updatePlaylist(playlistData, currentIndex) {
        this.elements.playlist.innerHTML = '';

        if (playlistData.length === 0) {
            const emptyItem = document.createElement('li');
            emptyItem.style.opacity = '0.7';
            emptyItem.style.textAlign = 'center';
            emptyItem.textContent = 'Toca ğŸ“ para agregar canciones al nido';
            this.elements.playlist.appendChild(emptyItem);
            return;
        }

        playlistData.forEach((track, index) => {
            const li = document.createElement('li');

            let durationText = '';
            if (track.duration > 0) {
                const minutes = Math.floor(track.duration / 60);
                const seconds = String(track.duration % 60).padStart(2, '0');
                durationText = ` (${minutes}:${seconds})`;
            }

            li.textContent = track.name + durationText;
            li.classList.toggle('active', index === currentIndex);

            li.addEventListener('click', () => {
                if (this.onTrackSelect) this.onTrackSelect(index);
            });

            this.elements.playlist.appendChild(li);
        });
    }

    updateStatus(message) {
        if (this.elements.status) {
            this.elements.status.textContent = message;
        }
    }

    updatePlayButton(isPlaying) {
        const textElement = this.elements.playPauseBtn.querySelector('.control-text');
        if (textElement) {
            textElement.innerHTML = isPlaying ? "SOL<br>â¸ï¸ PAUSA" : "SOL<br>â–¶ï¸ PLAY/PAUSE";
        }
    }

    showPajarito(duration = 2500) {
        if (this.elements.pajaritoOverlay) {
            this.elements.pajaritoOverlay.style.display = 'flex';
            setTimeout(() => {
                this.elements.pajaritoOverlay.style.display = 'none';
            }, duration);
        }
    }
