}6. ğŸ¯ main.js - COORDINADOR PRINCIPALjavascriptCopyDownloadclass BioTunerApp {
    constructor() {
        this.audioManager = new AudioManager();
        this.playlistManager = new PlaylistManager();
        this.uiManager = new UIManager();
        this.frequencyEngine = new FrequencyEngine();
        this.themeManager = new ThemeManager();

        this.initialize();
    }

    initialize() {
        // Configurar callbacks
        this.setupCallbacks();

        // Inicializar UI
        this.uiManager.initialize();

        // Configurar playlist manager
        this.playlistManager.onPlaylistUpdate = (playlist) => {
            this.uiManager.updatePlaylist(playlist, this.playlistManager.currentIndex);
        };

        this.playlistManager.onTrackChange = (track) => {
            if (track) {
                this.uiManager.updateStatus(`Listo: ${track.name}`);
            }
        };

        // Aplicar tema por defecto
        this.themeManager.applyTheme('solar-lunar');

        console.log("BioTuner App inicializado modularmente");
    }

    setupCallbacks() {
        // Configurar callbacks de UI
        this.uiManager.onPlayPause = () => this.handlePlayPause();
        this.uiManager.onStop = () => this.handleStop();
        this.uiManager.onNext = () => this.handleNext();
        this.uiManager.onPrevious = () => this.handlePrevious();
        this.uiManager.onLoadFiles = (files) => this.handleLoadFiles(files);
        this.uiManager.onFrequencyChange = (freq) => this.handleFrequencyChange(freq);
        this.uiManager.onTrackSelect = (index) => this.handleTrackSelect(index);
    }

    async handleLoadFiles(files) {
        if (files.length === 0) return;

        this.audioManager.init();
        this.uiManager.showPajarito(2000);

        const newTracks = await this.playlistManager.addFiles(files);

        if (this.playlistManager.currentIndex === -1 && newTracks.length > 0) {
            this.playlistManager.setCurrentIndex(0);
        }

        this.uiManager.updateStatus(`Se agregaron ${files.length} canciones. Total: ${this.playlistManager.playlistData.length}`);
    }

    handlePlayPause() {
        this.audioManager.init();

        const currentTrack = this.playlistManager.getCurrentTrack();
        if (!currentTrack || !currentTrack.buffer) {
            this.uiManager.updateStatus("No hay canciÃ³n cargada para reproducir");
            return;
        }

        if (!this.audioManager.isPlaying && !this.audioManager.isPaused) {
            // Play
            this.playCurrentTrack();
        } else if (this.audioManager.isPlaying) {
            // Pause
            this.audioManager.pausedAt = Math.max(0, this.audioManager.audioCtx.currentTime - this.audioManager.startTime);
            this.audioManager.stopSource();
            this.audioManager.isPlaying = false;
            this.audioManager.isPaused = true;
            this.uiManager.updatePlayButton(false);
            this.uiManager.updateStatus("Pausado");
        } else if (this.audioManager.isPaused) {
            // Resume
            this.playCurrentTrack();
        }
    }

    playCurrentTrack() {
        const currentTrack = this.playlistManager.getCurrentTrack();
        if (!currentTrack?.buffer) return;

        this.audioManager.sourceNode = this.audioManager.startSourceAt(
            this.audioManager.pausedAt,
            currentTrack.buffer,
            this.frequencyEngine.getCurrentFrequency()
        );

        if (this.audioManager.sourceNode) {
            this.audioManager.sourceNode.onended = () => {
                this.audioManager.isPlaying = false;
                this.audioManager.isPaused = false;
                this.audioManager.pausedAt = 0;
                this.uiManager.updatePlayButton(false);
                this.uiManager.updateStatus("CanciÃ³n terminada");
                this.uiManager.updatePlaylist(this.playlistManager.playlistData, this.playlistManager.currentIndex);
            };
        }

        this.uiManager.updatePlayButton(true);
        this.uiManager.updatePlaylist(this.playlistManager.playlistData, this.playlistManager.currentIndex);

        const freqInfo = this.frequencyEngine.getFrequencyInfo(this.frequencyEngine.getCurrentFrequency());
        this.uiManager.updateStatus(`Reproduciendo: ${currentTrack.name} | ${freqInfo.value}Hz ${freqInfo.name}`);
        this.uiManager.showPajarito(1000);
    }

    handleStop() {
        this.audioManager.stopSource();
        this.audioManager.isPlaying = false;
        this.audioManager.isPaused = false;
        this.audioManager.pausedAt = 0;
        this.uiManager.updatePlayButton(false);
        this.uiManager.updateStatus("Detenido");
        this.uiManager.updatePlaylist(this.playlistManager.playlistData, -1);
    }

    handleNext() {
        const newIndex = this.playlistManager.next();
        if (newIndex !== -1) {
            this.audioManager.pausedAt = 0;
            this.handleStop();
            this.playCurrentTrack();
        }
    }

    handlePrevious() {
        const newIndex = this.playlistManager.previous();
        if (newIndex !== -1) {
            this.audioManager.pausedAt = 0;
            this.handleStop();
            this.playCurrentTrack();
        }
    }

    handleFrequencyChange(newFrequency) {
        if (this.frequencyEngine.setFrequency(newFrequency)) {
            this.audioManager.changeFrequency(newFrequency);

            const freqInfo = this.frequencyEngine.getFrequencyInfo(newFrequency);
            const status = this.audioManager.isPlaying ? 
                `Frecuencia cambiada: ${newFrequency}Hz ${freqInfo.name}` :
                `Frecuencia preparada: ${newFrequency}Hz ${freqInfo.name}`;

            this.uiManager.updateStatus(status);
        }
    }

    handleTrackSelect(index) {
        this.playlistManager.setCurrentIndex(index);
        this.audioManager.pausedAt = 0;
        this.handleStop();
        this.playCurrentTrack();
    }

    // MÃ©todos adicionales para extensiÃ³n
    changeTheme(themeName) {
        return this.themeManager.applyTheme(themeName);
    }

    getCurrentState() {
        return {
            playing: this.audioManager.isPlaying,
            paused: this.audioManager.isPaused,
            currentTrack: this.playlistManager.getCurrentTrack(),
            currentFrequency: this.frequencyEngine.getCurrentFrequency(),
            currentTheme: this.themeManager.getCurrentTheme()
        };
    }
}

