let tiempoInactividad = 0;
let frecuenciaFantasmaActiva = false;

function actualizarCorresponsabilidad() {
  if (!isPlaying) {
    tiempoInactividad++;

    // Despu√©s de 30 segundos de inactividad, activar frecuencia fantasma
    if (tiempoInactividad > 30 && !frecuenciaFantasmaActiva) {
      activarFrecuenciaFantasma();
    }

    // Mensajes de "abono" seg√∫n el tiempo de inactividad
    const mensajesAbono = [
      "üå± El nido espera pacientemente...",
      "üí° ¬øNecesitas ayuda para comenzar?",
      "üéµ La m√∫sica puede ser un buen compa√±ero",
      "üåô A veces la quietud tambi√©n ense√±a"
    ];

    if (tiempoInactividad % 20 === 0) {
      const mensaje = mensajesAbono[Math.floor(Math.random() * mensajesAbono.length)];
      document.getElementById('mensajeEstado').textContent = mensaje;
    }
  } else {
    tiempoInactividad = 0;
    desactivarFrecuenciaFantasma();
  }
}

function activarFrecuenciaFantasma() {
  frecuenciaFantasmaActiva = true;
  // Implementar frecuencia sutil de 432Hz a volumen muy bajo
  document.getElementById('frecuenciaFantasma').textContent = "üéµ Frecuencia fantasma: Activada (432Hz)";
}

function desactivarFrecuenciaFantasma() {
  frecuenciaFantasmaActiva = false;
  document.getElementById('frecuenciaFantasma').textContent = "üéµ Frecuencia fantasma: Inactiva";
