    <script>
        class BiotunerIMCORVEC {
            constructor() {
                this.audioCtx = null;
                this.oscillator = null;
                this.gainNode = null;
                this.oscFlauta = null;
                this.gainFlauta = null;
                this.freqActual = null;
                this.proto544Activo = true;
                this.reproduciendo = false;
                this.tiempoInicio = null;
                this.timerInterval = null;

                this.iniciarAudio();
                this.inicializarUI();
                this.generarFrecuencias();
            }

            iniciarAudio() {
                try {
                    this.audioCtx = new (window.AudioContext || window.webkitAudioContext)();
                } catch (e) {
                    console.error("Error AudioContext:", e);
                    alert("Tu navegador no soporta audio avanzado. Usa Chrome o Firefox.");
                }
            }

            generarFrecuencias() {
                const container = document.getElementById('freqGrid');

                // Frecuencias principales de la ventana 420-452Hz
                const frecuencias = [];
                for (let i = 420; i <= 452; i++) {
                    frecuencias.push(i);
                }

                frecuencias.forEach(freq => {
                    const btn = document.createElement('button');
                    btn.className = 'freq-btn';
                    btn.textContent = `${freq} Hz`;
                    btn.dataset.freq = freq;

                    btn.addEventListener('click', () => {
                        this.seleccionarFrecuencia(freq);
                    });

                    container.appendChild(btn);
                });

                // Seleccionar 432Hz por defecto (para Protocolo 544)
                this.seleccionarFrecuencia(432);
            }

            seleccionarFrecuencia(frecuencia) {
                this.freqActual = frecuencia;

                // Actualizar UI
                document.querySelectorAll('.freq-btn').forEach(btn => {
                    btn.classList.remove('active');
                });
                event.target.classList.add('active');
                document.getElementById('freqActual').textContent = frecuencia;

                // Si está reproduciendo, actualizar frecuencia
                if (this.reproduciendo) {
                    this.detener();
                    this.reproducir();
                }
            }

            reproducir() {
                if (!this.freqActual) {
                    alert("Selecciona una frecuencia primero");
                    return;
                }

                this.detener();

                try {
                    // Oscilador principal
                    this.oscillator = this.audioCtx.createOscillator();
                    this.gainNode = this.audioCtx.createGain();

                    this.oscillator.type = 'sine';
                    this.oscillator.frequency.value = this.freqActual;
                    this.gainNode.gain.value = document.getElementById('sliderBase').value / 100;

                    this.oscillator.connect(this.gainNode);
                    this.gainNode.connect(this.audioCtx.destination);

                    this.oscillator.start();

                    // Flauta 544Hz si el protocolo está activo
                    if (this.proto544Activo) {
                        this.activarFlauta544();
                    }

                    this.reproduciendo = true;
                    this.iniciarTemporizador();

                } catch (e) {
                    console.error("Error reproduciendo:", e);
                }
            }

            activarFlauta544() {
                try {
                    this.oscFlauta = this.audioCtx.createOscillator();
                    this.gainFlauta = this.audioCtx.createGain();

                    this.oscFlauta.type = 'sine';
                    this.oscFlauta.frequency.value = 544;
                    this.gainFlauta.gain.value = document.getElementById('sliderFlauta').value / 100;

                    this.oscFlauta.connect(this.gainFlauta);
                    this.gainFlauta.connect(this.audioCtx.destination);

                    this.oscFlauta.start();

                } catch (e) {
                    console.error("Error flauta 544:", e);
                }
            }

            detener() {
                if (this.oscillator) {
                    this.oscillator.stop();
                    this.oscillator = null;
                }
                if (this.oscFlauta) {
                    this.oscFlauta.stop();
                    this.oscFlauta = null;
                }
                this.reproduciendo = false;
                this.detenerTemporizador();
            }

            iniciarTemporizador() {
                this.tiempoInicio = Date.now();
                this.timerInterval = setInterval(() => {
                    const segundos = Math.floor((Date.now() - this.tiempoInicio) / 1000);
                    const minutos = Math.floor(segundos / 60);
                    const segs = segundos % 60;
                    document.getElementById('tiempoSesion').textContent = 
                        `${minutos.toString().padStart(2, '0')}:${segs.toString().padStart(2, '0')}`;
                }, 1000);
            }

            detenerTemporizador() {
                if (this.timerInterval) {
                    clearInterval(this.timerInterval);
                    this.timerInterval = null;
                }
                document.getElementById('tiempoSesion').textContent = "00:00";
            }

            inicializarUI() {
                // Controles principales
                document.getElementById('btnPlay').addEventListener('click', () => this.reproducir());
                document.getElementById('btnStop').addEventListener('click', () => this.detener());

                // Sliders de volumen
                document.getElementById('sliderBase').addEventListener('input', (e) => {
                    document.getElementById('volBase').textContent = e.target.value;
                    if (this.gainNode) {
                        this.gainNode.gain.value = e.target.value / 100;
                    }
                });

                document.getElementById('sliderFlauta').addEventListener('input', (e) => {
                    document.getElementById('volFlauta').textContent = e.target.value;
                    if (this.gainFlauta) {
                        this.gainFlauta.gain.value = e.target.value / 100;
                    }
                });

                // Protocolo 544
                document.getElementById('proto544').addEventListener('click', () => {
                    this.proto544Activo = !this.proto544Activo;
                    const proto = document.getElementById('proto544');

                    if (this.proto544Activo) {
                        proto.style.background = 'linear-gradient(45deg, #f72585, #b5179e)';
                        proto.innerHTML = '<h3>🔮 PROTOCOLO 544 ACTIVO</h3><p>Base 432Hz + Flauta 544Hz | Volumen: 25%</p>';
