const FrequenciesModule = (function() {
  let currentFrequency = 440;
  let binauralActive = false;
  let binauralNodes = null;

  function setFrequency(freq) {
    currentFrequency = freq;
    // Aplicar el cambio de frecuencia a la reproducciÃ³n actual
