    const audioCtx = AudioModule.audioCtx; // Necesitamos exponer audioCtx en AudioModule para esto
    if (!audioCtx) return;

    const oscillatorLeft = audioCtx.createOscillator();
    const oscillatorRight = audioCtx.createOscillator();
    const gainLeft = audioCtx.createGain();
    const gainRight = audioCtx.createGain();
    const merger = audioCtx.createChannelMerger(2);

    oscillatorLeft.frequency.value = freqBase;
    oscillatorRight.frequency.value = freqBase + deltaFreq;

    gainLeft.gain.value = 0.3;
    gainRight.gain.value = 0.3;

    oscillatorLeft.connect(gainLeft);
    oscillatorRight.connect(gainRight);
    gainLeft.connect(merger, 0, 0);
    gainRight.connect(merger, 0, 1);
    merger.connect(audioCtx.destination);

    oscillatorLeft.start();
    oscillatorRight.start();

    binauralNodes = { oscillatorLeft, oscillatorRight, gainLeft, gainRight };
    binauralActive = true;
  }

  function stopBinaural() {
    if (binauralNodes) {
      binauralNodes.oscillatorLeft.stop();
      binauralNodes.oscillatorRight.stop();
      binauralNodes = null;
      binauralActive = false;
    }
  }

  return {
    setFrequency,
    getCurrentFrequency,
    createBinaural,
    stopBinaural
