const AudioModule = (function() {
  let audioCtx = null;
  let masterGain = null;
  let sourceNode = null;
  let currentBuffer = null;
  let isPlaying = false;
  let isPaused = false;
  let startTime = 0;
  let pausedAt = 0;
  let volume = 0.7;

  function init() {
    if (!audioCtx) {
      audioCtx = new (window.AudioContext || window.webkitAudioContext)();
      masterGain = audioCtx.createGain();
      masterGain.gain.value = volume;
      masterGain.connect(audioCtx.destination);
    }
  }

  function playBuffer(buffer, offset = 0, playbackRate = 1) {
    if (!audioCtx) init();

    stopSourceNode();

    sourceNode = audioCtx.createBufferSource();
    sourceNode.buffer = buffer;
    sourceNode.playbackRate.value = playbackRate;
    sourceNode.connect(masterGain);

    sourceNode.onended = () => {
      isPlaying = false;
      isPaused = false;
      pausedAt = 0;
    };

    sourceNode.start(0, offset);
    startTime = audioCtx.currentTime - offset;
    isPlaying = true;
    isPaused = false;
  }

  function stopSourceNode() {
    if (sourceNode) {
      try {
        sourceNode.stop();
      } catch (e) {}
      sourceNode = null;
    }
  }

  function pause() {
    if (!audioCtx || !isPlaying) return;

    pausedAt = audioCtx.currentTime - startTime;
    stopSourceNode();
    isPlaying = false;
    isPaused = true;
  }

  function stop() {
    stopSourceNode();
    isPlaying = false;
    isPaused = false;
    pausedAt = 0;
  }

  function setPlaybackRate(rate) {
    if (sourceNode) {
      const now = audioCtx.currentTime;
      sourceNode.playbackRate.cancelScheduledValues(now);
      sourceNode.playbackRate.linearRampToValueAtTime(rate, now + 0.2);
    }
  }

  function setVolume(value) {
    volume = value;
    if (masterGain) {
      masterGain.gain.value = value;
    }
  }

  function getCurrentTime() {
    if (isPlaying) {
      return audioCtx.currentTime - startTime;
    } else {
      return pausedAt;
    }
  }

  return {
    init,
    playBuffer,
    pause,
    stop,
    setPlaybackRate,
    setVolume,
    getCurrentTime,
    get isPlaying() { return isPlaying; },
    get isPaused() { return isPaused; }
  };
})();Ahora, vamos a crear el m√≥dulo de playlist. Este m√≥dulo depender√° del m√≥dulo de audio para reproducir los buffers.javascriptCopyDownload// M√≥dulo de Playlist
const PlaylistModule = (function() {
  let playlist = [];
  let currentIndex = -1;

  function addFile(file, buffer) {
    playlist.push({ file, buffer });
  }

  function getCurrent() {
    return playlist[currentIndex];
  }

  function setCurrentIndex(index) {
    if (index >= 0 && index < playlist.length) {
      currentIndex = index;
    }
  }

  function playCurrent(offset = 0, playbackRate = 1) {
    const current = getCurrent();
    if (current && current.buffer) {
      AudioModule.playBuffer(current.buffer, offset, playbackRate);
    }
  }

  function next() {
    if (playlist.length === 0) return;
    currentIndex = (currentIndex + 1) % playlist.length;
  }

  function prev() {
    if (playlist.length === 0) return;
    currentIndex = (currentIndex - 1 + playlist.length) % playlist.length;
  }

  function getPlaylist() {
    return playlist;
  }

  function clear() {
    playlist = [];
    currentIndex = -1;
  }

  return {
    addFile,
    getCurrent,
    setCurrentIndex,
    playCurrent,
    next,
    prev,
    getPlaylist,
    clear
  };
})();Luego, el m√≥dulo de frecuencias. Este m√≥dulo manejar√° las frecuencias arm√≥nicas y el arrastre binaural.javascriptCopyDownload// M√≥dulo de Frecuencias
const FrequenciesModule = (function() {
  let currentFrequency = 440;
  let binauralActive = false;
  let binauralNodes = null;

  function setFrequency(freq) {
    currentFrequency = freq;
