export class AudioCore {
  constructor() {
    this.audioCtx = null;
    this.masterGain = null;
    this.initialized = false;
  }

  init() {
    if (this.initialized) return;

    this.audioCtx = new (window.AudioContext || window.webkitAudioContext)();
    this.masterGain = this.audioCtx.createGain();
    this.masterGain.gain.value = 0.7;
    this.masterGain.connect(this.audioCtx.destination);

    this.initialized = true;
  }

  createSourceNode(buffer) {
    if (!this.initialized) this.init();

    const source = this.audioCtx.createBufferSource();
    source.buffer = buffer;
    source.connect(this.masterGain);
    return source;
  }

  setVolume(level) {
    if (this.masterGain) {
      this.masterGain.gain.value = level;
    }
  }
}üéµ M√ìDULO 2: REPRODUCTORjavascriptCopyDownload// modulo_reproductor.js
export class Reproductor {
  constructor(audioCore) {
    this.audioCore = audioCore;
    this.sourceNode = null;
    this.isPlaying = false;
    this.isPaused = false;
    this.startTime = 0;
    this.pausedAt = 0;
    this.currentBuffer = null;
  }

  play(buffer, offset = 0, playbackRate = 1) {
    if (this.isPlaying) return;

    this.stop();

    this.sourceNode = this.audioCore.createSourceNode(buffer);
    this.sourceNode.playbackRate.value = playbackRate;
    this.sourceNode.start(0, offset);

    this.startTime = this.audioCore.audioCtx.currentTime - offset;
    this.isPlaying = true;
    this.isPaused = false;
    this.currentBuffer = buffer;
  }

  pause() {
    if (!this.isPlaying) return;

    this.pausedAt = this.audioCore.audioCtx.currentTime - this.startTime;
    this.stopSourceNode();
    this.isPlaying = false;
    this.isPaused = true;
  }

