    if (!this.isPaused) return;
    this.play(this.currentBuffer, this.pausedAt);
  }

  stop() {
    this.stopSourceNode();
    this.isPlaying = false;
    this.isPaused = false;
    this.pausedAt = 0;
  }

  stopSourceNode() {
    if (this.sourceNode) {
      try { this.sourceNode.stop(); } catch(e) {}
      this.sourceNode = null;
    }
  }

  setPlaybackRate(rate) {
    if (this.sourceNode) {
      const now = this.audioCore.audioCtx.currentTime;
      this.sourceNode.playbackRate.linearRampToValueAtTime(rate, now + 0.2);
