// =============================================
// CONFIGURACI√ìN DE FRECUENCIAS BASE
// =============================================
const FRECUENCIAS_BASE = [
  { hz: 432, label: "Base 1" },
  { hz: 436, label: "Base 2" },
  { hz: 440, label: "Base 3" },
  { hz: 444, label: "Base 4" },
  { hz: 448, label: "Base 5" },
  { hz: 452, label: "Base 6" },
  { hz: 456, label: "Base 7" }
];

// =============================================
// M√ìDULO PRINCIPAL - REPRODUCTOR CON FRECUENCIAS
// =============================================

class AudioPlayer {
  constructor() {
    this.audioContext = null;
    this.audioSource = null;
    this.playlist = [];
    this.currentIndex = -1;
    this.isPlaying = false;
    this.isPaused = false;
    this.frecuenciaActual = 440; // Default a Base 3

    this.initializeElements();
    this.setupEventListeners();
    this.renderFrecuencias();
    this.actualizarDisplayFrecuencia();
  }

  initializeElements() {
    this.playPauseBtn = document.getElementById('playPauseBtn');
    this.stopBtn = document.getElementById('stopBtn');
    this.prevBtn = document.getElementById('prevBtn');
    this.nextBtn = document.getElementById('nextBtn');
    this.loadBtn = document.getElementById('loadBtn');
    this.fileInput = document.getElementById('fileInput');
    this.status = document.getElementById('status');
    this.playlistEl = document.getElementById('playlist');
    this.frecuenciasContainer = document.getElementById('frecuenciasContainer');
    this.frecuenciaDisplay = document.getElementById('frecuenciaDisplay');
  }

  setupEventListeners() {
    this.playPauseBtn.addEventListener('click', () => this.togglePlayPause());
    this.stopBtn.addEventListener('click', () => this.stop());
    this.prevBtn.addEventListener('click', () => this.previousTrack());
    this.nextBtn.addEventListener('click', () => this.nextTrack());
    this.loadBtn.addEventListener('click', () => this.fileInput.click());
    this.fileInput.addEventListener('change', (e) => this.loadFiles(e));
  }

  renderFrecuencias() {
    this.frecuenciasContainer.innerHTML = '';

    FRECUENCIAS_BASE.forEach((frec, index) => {
      const btn = document.createElement('button');
      btn.className = 'frecuencia-btn';
      btn.disabled = this.isPlaying || this.isPaused;

      if (frec.hz === this.frecuenciaActual) {
        btn.classList.add('active');
      }

      btn.innerHTML = `
        <div class="frecuencia-hz">${frec.hz}Hz</div>
        <div class="frecuencia-label">${frec.label}</div>
      `;

      btn.addEventListener('click', () => {
        if (!this.isPlaying && !this.isPaused) {
          this.cambiarFrecuencia(frec.hz, frec.label);
        }
      });

      this.frecuenciasContainer.appendChild(btn);
    });
  }

  cambiarFrecuencia(nuevaFrecuencia, label) {
    this.frecuenciaActual = nuevaFrecuencia;
    this.renderFrecuencias();
    this.actualizarDisplayFrecuencia();
    this.updateStatus();
  }

  actualizarDisplayFrecuencia() {
    const frecuencia = FRECUENCIAS_BASE.find(f => f.hz === this.frecuenciaActual);
    this.frecuenciaDisplay.textContent = `${this.frecuenciaActual}Hz (${frecuencia.label})`;
  }

  async initializeAudio() {
    if (!this.audioContext) {
      this.audioContext = new (window.AudioContext || window.webkitAudioContext)();

      if (this.audioContext.state === 'suspended') {
        await this.audioContext.resume();
      }
    }
    return true;
  }

  async loadFiles(event) {
    const files = Array.from(event.target.files);
    if (files.length === 0) return;

    this.status.textContent = `Cargando ${files.length} archivo(s)...`;

    // Inicializar audio en la primera carga
    if (!this.audioContext) {
      await this.initializeAudio();
    }

    let archivosCargados = 0;

    for (const file of files) {
      try {
        // Leer el archivo como ArrayBuffer
        const arrayBuffer = await this.readFileAsArrayBuffer(file);

        // Decodificar el audio
        const audioBuffer = await this.decodeAudioData(arrayBuffer);

        this.playlist.push({
          name: file.name,
          buffer: audioBuffer
        });

        archivosCargados++;
        this.renderPlaylist();
        this.status.textContent = `Cargado: ${file.name} (${archivosCargados}/${files.length})`;

      } catch (error) {
        console.error('Error cargando archivo:', error);
        this.status.textContent = `Error cargando: ${file.name}`;
      }
    }

    if (archivosCargados > 0) {
      this.status.textContent = `¬°Listo! ${archivosCargados} archivo(s) cargado(s)`;

      if (this.currentIndex === -1) {
        this.currentIndex = 0;
        this.selectTrackInPlaylist(0);
      }
    } else {
      this.status.textContent = 'No se pudieron cargar los archivos';
    }

    this.updateStatus();
    this.fileInput.value = '';
  }

  // Funci√≥n auxiliar para leer archivos
  readFileAsArrayBuffer(file) {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = () => resolve(reader.result);
      reader.onerror = () => reject(new Error('Error leyendo archivo'));
      reader.readAsArrayBuffer(file);
    });
  }

  // Funci√≥n auxiliar para decodificar audio
  decodeAudioData(arrayBuffer) {
    return new Promise((resolve, reject) => {
      this.audioContext.decodeAudioData(
        arrayBuffer,
        buffer => resolve(buffer),
        error => reject(new Error('Error decodificando audio'))
      );
    });
  }

  async togglePlayPause() {
    if (this.playlist.length === 0) {
      this.status.textContent = "Primero carga alg√∫n archivo de audio";
      return;
    }

    await this.initializeAudio();

    if (this.isPlaying) {
      this.pause();
    } else if (this.isPaused) {
      this.resume();
    } else {
      this.reproducirTrack(this.currentIndex !== -1 ? this.currentIndex : 0);
    }
  }

  reproducirTrack(index, offset = 0) {
    this.detenerFuenteAudio();

    this.currentIndex = index;
    const track = this.playlist[this.currentIndex];

    this.audioSource = this.audioContext.createBufferSource();
    this.audioSource.buffer = track.buffer;

    // APLICAR FRECUENCIA BASE mediante playbackRate
    const rate = this.frecuenciaActual / 440;
    this.audioSource.playbackRate.value = rate;

    this.audioSource.connect(this.audioContext.destination);

    this.audioSource.start(0, offset);
    this.audioSource.onended = () => {
      if (this.isPlaying) {
        this.nextTrack();
      }
    };

    this.isPlaying = true;
    this.isPaused = false;
    this.startTime = this.audioContext.currentTime - offset;
    this.playPauseBtn.textContent = '‚è∏';

    // DESHABILITAR CAMBIO DE FRECUENCIAS DURANTE REPRODUCCI√ìN
    this.renderFrecuencias();

    this.selectTrackInPlaylist(index);
    this.updateStatus();
  }

  pause() {
    if (this.isPlaying) {
      this.detenerFuenteAudio();
      this.isPlaying = false;
      this.isPaused = true;
      this.playPauseBtn.textContent = '‚ñ∂Ô∏è';
      this.updateStatus();
    }
  }

  resume() {
    if (this.isPaused) {
      this.reproducirTrack(this.currentIndex, this.obtenerTiempoActual());
    }
  }

  stop() {
    this.detenerFuenteAudio();
    this.isPlaying = false;
    this.isPaused = false;
    this.playPauseBtn.textContent = '‚ñ∂Ô∏è';

    // HABILITAR CAMBIO DE FRECUENCIAS AL DETENER
    this.renderFrecuencias();

    this.updateStatus();
  }

  detenerFuenteAudio() {
    if (this.audioSource) {
      try {
        this.audioSource.stop();
      } catch (e) {}
      this.audioSource.disconnect();
      this.audioSource = null;
    }
  }

  obtenerTiempoActual() {
    if (this.isPlaying && this.audioContext) {
      return this.audioContext.currentTime - this.startTime;
    }
    return 0;
  }

  nextTrack() {
    if (this.playlist.length === 0) return;
    const nextIndex = (this.currentIndex + 1) % this.playlist.length;
    this.reproducirTrack(nextIndex);
  }

  previousTrack() {
    if (this.playlist.length === 0) return;
    let prevIndex = this.currentIndex - 1;
    if (prevIndex < 0) prevIndex = this.playlist.length - 1;
    this.reproducirTrack(prevIndex);
  }

  renderPlaylist() {
    this.playlistEl.innerHTML = '';
    this.playlist.forEach((track, index) => {
      const li = document.createElement('li');
      li.textContent = track.name;
      li.dataset.index = index;
      li.onclick = () => {
        if (!this.isPlaying && !this.isPaused) {
          this.reproducirTrack(index);
        }
      };
      this.playlistEl.appendChild(li);
    });
  }

  selectTrackInPlaylist(index) {
    const allItems = this.playlistEl.querySelectorAll('li');
    allItems.forEach(li => li.classList.remove('active'));
    const activeItem = this.playlistEl.querySelector(`li[data-index="${index}"]`);
    if (activeItem) activeItem.classList.add('active');
  }

  updateStatus() {
    const trackName = this.currentIndex !== -1 ? this.playlist[this.currentIndex].name : 'Ninguna';
    const state = this.isPlaying ? 'Reproduciendo' : (this.isPaused ? 'Pausado' : 'Detenido');
    this.status.textContent = `${state}: ${trackName}`;
  }
}

