    <script>
        // Elementos DOM
        const audioElement = document.getElementById('audioElement');
        const freqElement = document.getElementById('freqElement');
        const playBtn = document.getElementById('playBtn');
        const progressBar = document.getElementById('progressBar');
        const timeDisplay = document.getElementById('timeDisplay');
        const fileInput = document.getElementById('fileInput');
        const playlistContainer = document.getElementById('playlistContainer');
        const baseFreqSlider = document.getElementById('baseFreqSlider');
        const baseFreqValue = document.getElementById('baseFreqValue');
        const capasTexto = document.getElementById('capasTexto');

        // Estado del reproductor
        let playlist = [];
        let currentTrackIndex = 0;
        let isPlaying = false;
        let frecuenciasActivas = [];

        // Configurar audio element
        audioElement.addEventListener('loadedmetadata', function() {
            progressBar.max = audioElement.duration;
            updateTimeDisplay();
        });

        audioElement.addEventListener('timeupdate', function() {
            progressBar.value = audioElement.currentTime;
            updateTimeDisplay();
        });

        audioElement.addEventListener('ended', nextTrack);

        // Controles principales
        function togglePlay() {
            if (isPlaying) {
                audioElement.pause();
                freqElement.pause();
                playBtn.innerHTML = 'â–¶ï¸ Play';
                isPlaying = false;
            } else {
                audioElement.play().catch(e => console.log('Error al reproducir:', e));
                if (frecuenciasActivas.length > 0) {
                    freqElement.play().catch(e => console.log('Error en frecuencias:', e));
                }
                playBtn.innerHTML = 'â¸ Pause';
                isPlaying = true;
            }
        }

        function previousTrack() {
            if (playlist.length === 0) return;
            currentTrackIndex = (currentTrackIndex - 1 + playlist.length) % playlist.length;
            loadTrack(currentTrackIndex);
        }

        function nextTrack() {
            if (playlist.length === 0) return;
            currentTrackIndex = (currentTrackIndex + 1) % playlist.length;
            loadTrack(currentTrackIndex);
        }

        function loadTrack(index) {
            if (playlist.length === 0) return;

            const wasPlaying = isPlaying;
            const track = playlist[index];
            audioElement.src = track.url;
            audioElement.load();

            if (wasPlaying) {
                setTimeout(() => {
                    audioElement.play().catch(e => console.log('Error al reproducir:', e));
                    if (frecuenciasActivas.length > 0) {
                        freqElement.play().catch(e => console.log('Error en frecuencias:', e));
                    }
                    isPlaying = true;
                    playBtn.innerHTML = 'â¸ Pause';
                }, 100);
            }

            document.querySelectorAll('.playlist-item').forEach((item, i) => {
                item.classList.toggle('current', i === index);
            });
        }

        // SintonizaciÃ³n fina (430-450 Hz)
        baseFreqSlider.addEventListener('input', function() {
            const freq = parseFloat(baseFreqSlider.value);
            baseFreqValue.textContent = freq;

            // Ajustar la velocidad de reproducciÃ³n para cambiar la frecuencia base
            const factor = freq / 440; // 440 Hz es el estÃ¡ndar
            audioElement.playbackRate = factor;
        });

        // ActivaciÃ³n de frecuencias puras IMCORVEC
        function activateFreq(frecuencia) {
            if (!frecuenciasActivas.includes(frecuencia)) {
                frecuenciasActivas.push(frecuencia);
                actualizarCapas();
            }
        }

        function resetFreqs() {
            frecuenciasActivas = [];
            freqElement.pause();
            actualizarCapas();
        }

        function actualizarCapas() {
            capasTexto.textContent = frecuenciasActivas.length > 0 ? 
                frecuenciasActivas.join(' Hz, ') + ' Hz' : 'Ninguna';

            if (frecuenciasActivas.length > 0) {
                // AquÃ­ irÃ­a la generaciÃ³n de las frecuencias puras superpuestas
                // (Para una implementaciÃ³n completa necesitarÃ­amos Web Audio API)
                console.log('Frecuencias activas:', frecuenciasActivas);

                if (isPlaying) {
                    freqElement.play().catch(e => console.log('Error en frecuencias:', e));
                }
            } else {
                freqElement.pause();
            }
        }

        // Barra de progreso
        progressBar.addEventListener('input', function() {
            audioElement.currentTime = progressBar.value;
        });

        function updateTimeDisplay() {
            const current = formatTime(audioElement.currentTime);
            const total = formatTime(audioElement.duration);
            timeDisplay.textContent = `${current} / ${total}`;
        }

        function formatTime(seconds) {
            if (isNaN(seconds)) return '0:00';
            const min = Math.floor(seconds / 60);
            const sec = Math.floor(seconds % 60);
            return `${min}:${sec.toString().padStart(2, '0')}`;
        }

        // Lista de reproducciÃ³n
        fileInput.addEventListener('change', function(e) {
            const files = Array.from(e.target.files);

            files.forEach((file, index) => {
                const url = URL.createObjectURL(file);
                const trackIndex = playlist.length;

                playlist.push({
                    name: file.name,
                    url: url
                });

                const item = document.createElement('div');
                item.className = 'playlist-item';
                item.textContent = file.name;
                item.onclick = () => {
                    currentTrackIndex = trackIndex;
                    loadTrack(currentTrackIndex);
                    setTimeout(() => {
                        audioElement.play().catch(e => console.log('Error al reproducir:', e));
                        if (frecuenciasActivas.length > 0) {
                            freqElement.play().catch(e => console.log('Error en frecuencias:', e));
                        }
                        isPlaying = true;
                        playBtn.innerHTML = 'â¸ Pause';
                    }, 100);
                };
                playlistContainer.appendChild(item);
            });

            if (playlist.length === files.length) {
                loadTrack(0);
                setTimeout(() => {
                    audioElement.play().catch(e => console.log('Error al reproducir:', e));
                    isPlaying = true;
                    playBtn.innerHTML = 'â¸ Pause';
                }, 100);
            }
        });
    </script>
