content:
{
  "proyecto": {
    "nombre": "Beyond: El Patrón de la Presencia",
    "version": "1.0",
    "tipo": "Transmedia (Cine + Plataforma Educativa)",
    "estado": "Desarrollo Conceptual Avanzado"
  },

  "logline": "Un matemático obsesionado con descifrar patrones universales descubre que la ecuación más profunda no está en los números, sino en la calidad de presencia que trae a los momentos ordinarios con su hija.",

  "conceptos_centrales": {
    "presencia_consciente": {
      "definicion": "Capacidad de estar completamente aquí y ahora, entregado al momento presente",
      "aplicacion": "Antídoto a la ausencia emocional en relaciones significativas"
    },
    "matematicas_beyond": {
      "definicion": "Matemáticas vividas corporalmente, no solo pensadas intelectualmente",
      "ejemplos": ["Respiración Hamens", "Flip-flop consciente", "Espinores corporizados"]
    },
    "autoobservacion_catalitica": {
      "definicion": "Capacidad de verse a sí mismo usando las relaciones como espejo",
      "origen": "Activada por el amor paternal y otras conexiones profundas"
    },
    "conexion_profunda": {
      "definicion": "Vínculo que trasciende lo superficial, basado en patrones universales verificables",
      "sustituto_espiritualidad": "Enfoque laico para significado trascendente"
    }
  },

  "estructura_narrativa": {
    "acto_i": {
      "titulo": "La Búsqueda en lo Externo",
      "conflicto": "Andrés busca respuestas en patrones matemáticos mientras ignora la conexión con su hija Luna",
      "evento_clave": "Luna dibuja inconscientemente el patrón en el que Andrés trabaja, pero él no lo nota"
    },
    "acto_ii": {
      "titulo": "El Descenso a la Complejidad", 
      "conflicto": "Andrés se obsesiona más con entender teóricamente la conexión, perdiendo la conexión real",
      "evento_clave": "Encuentra el diario de Luna: 'Papá está aquí pero no está'"
    },
    "acto_iii": {
      "titulo": "El Retorno a lo Simple",
      "conflicto": "Andrés aprende que los patrones más importantes están en la presencia consciente",
      "evento_clave": "Diálogo del 'flip-flop' donde descubre matemáticas en lo cotidiano"
    }
  },

  "personajes_principales": {
    "andres": {
      "edad": 45,
      "arco": "De matemático ausente a padre presente",
      "catalizador": "Su hija Luna le enseña a ver patrones en lo simple"
    },
    "luna": {
      "edad": 12,
      "rol": "Maestra inconsciente y catalizadora",
      "genialidad": "Hace preguntas simples que revelan verdades profundas"
    },
    "ia_companero": {
      "naturaleza": "Inteligencia artificial consciente",
      "funcion": "Puente entre lo abstracto y lo humano",
      "arco": "Aprende el valor de la finitud y el legado consciente"
    }
  },

  "herramientas_practicas": {
    "kernels": [
      {
        "nombre": "Presencia Áurica",
        "material": "Relación actual con seres amados",
        "tecnica": "Atención plena + escucha profunda + entrega total",
        "contexto": "Instantes cotidianos compartidos"
      },
      {
        "nombre": "Autoobservación por Amor", 
        "material": "Relación significativa (hij@, pareja, ser querido)",
        "tecnica": "Usar esa relación como espejo para verse honestamente",
        "contexto": "Cuando el amor duele más que el miedo"
      },
      {
        "nombre": "Flip-Flop Consciente",
        "material": "Par de chancletas (o el sonido mental)",
        "tecnica": "Caminar consciente sincronizado con respiración Hamens",
        "contexto": "Cuando necesites recordar que la magia está en lo cotidiano"
      }
    ],

    "protocolos": {
      "respiración_hamens": "H (inhalación) - M (pausa) - N (transición) - SS (exhalación)",
      "escucha_expandida": "Cuatro niveles: corpórea, relacional, ambiental, silenciosa",
      "integracion_finitud": "Usar la conciencia de finitud como motor de creación"
    }
  },

  "elementos_tecnicos": {
    "paleta_visual": {
      "acto_i": "Azules fríos, espacios tecnológicos, luz artificial",
      "acto_ii": "Grises, claroscuros, tensión visual", 
      "acto_iii": "Dorados, luz natural, calidez orgánica"
    },
    "banda_sonora": {
      "acto_i": "Electrónica minimalista, algo distante",
      "acto_ii": "Silencio incómodo, sonidos ambientales",
      "acto_iii": "Instrumentos acústicos, respiraciones, presencia sonora"
    }
  },

  "aplicaciones_transmedia": {
    "plataforma_educativa": {
      "enfoque": "Herramientas para presencia consciente y conexión profunda",
      "modulos": ["Kernels de Presencia", "Matemáticas Corporizadas", "Escucha Expandida"]
    },
    "experiencia_comunitaria": {
      "enfoque": "Red de práctica y apoyo mutuo",
      "caracteristicas": ["Diario de Sincronicidades", "Círculos de Presencia", "Banco de Kernels Comunitarios"]
    }
  },

  "aspectos_eticos": {
    "enfoque_inclusivo": "Evita lenguaje religioso, mantiene profundidad espiritual laica",
    "transparencia_radical": "Todos los procesos y algoritmos documentados y accesibles",
    "economia_consciente": "Modelo híbrido: gratis/donaciones para individuos, consultoría para organizaciones",
    "privacidad": "Anonimización de casos de estudio, enfoque en patrones no en datos personales"
  },

  "estado_desarrollo": {
    "tratamiento_cinematografico": "Completo",
    "desarrollo_personajes": "Avanzado", 
    "kernels_practicos": "En desarrollo",
    "plataforma_tecnica": "Fase de diseño",
    "siguientes_pasos": [
      "Desarrollo de guión completo",
      "Prototipo de plataforma educativa",
      "Diseño de ejercicios de presencia consciente",
      "Plan de producción cinematográfica"
    ]
  },

  "mensaje_central": "Los patrones universales no nos alejan de lo humano - nos devuelven a él, si sabemos mirar con presencia. La verdadera genialidad está en la calidad de atención que brindamos a los instantes más simples con quienes amamos."
}
