            color: var(--primary);
            font-size: 2.5em;
            margin-bottom: 10px;
        }

        .header .version {
            color: var(--accent);
            font-size: 1.1em;
        }

        /* M√ìDULO REPRODUCTOR */
        .reproductor-modular {
            background: var(--card-bg);
            border-radius: 15px;
            padding: 25px;
            margin-bottom: 30px;
            box-shadow: 0 4px 20px rgba(0,0,0,0.1);
            border: 1px solid var(--border);
        }

        .reproductor-header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            margin-bottom: 25px;
            padding-bottom: 15px;
            border-bottom: 2px solid var(--border);
        }

        .reproductor-title {
            font-size: 1.8em;
            color: var(--primary);
        }

        .status-indicator {
            padding: 8px 16px;
            background: var(--success);
            color: white;
            border-radius: 20px;
            font-size: 0.9em;
        }

        .frecuencias-grid {
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(100px, 1fr));
            gap: 12px;
            margin-bottom: 25px;
        }

        .frecuencia-btn {
            padding: 15px 10px;
            background: linear-gradient(135deg, var(--secondary) 0%, var(--accent) 100%);
            color: white;
            border: none;
            border-radius: 10px;
            cursor: pointer;
            font-size: 1.1em;
